// Devontae Reid// Lab #5 CIS 180#include <iostream>#include <iomanip>using namespace std;void displayColumnTitle();float calculateBalanceDue(float balanceDue, float paymentAmt);void displayBalance(int month, float balanceDue);int main(){    const float PAY_RATE = 0.1;        string custName;    int acctNum;    float balanceDue;        cout << fixed << setprecision(2);        cout << "Enter Account Number (-1 to terminate the input): ";    cin >> acctNum;        while(acctNum != -1)    {        cout << "Please enter your name: ";        cin.ignore();        getline(cin, custName);                cout << "Enter balance due: ";        cin >> balanceDue;                displayColumnTitle();                int paymentAmt = balanceDue * PAY_RATE;            int months = 1;        while (months <= 10) {            float balance = calculateBalanceDue(balanceDue, paymentAmt);            displayBalance(months, balance);            balanceDue = balance;            months += 1;        }                cout << endl << "Enter Account Number (-1 to terminate the input): ";        cin >> acctNum;    }}void displayColumnTitle(){    //Display the column title    cout << setw(5) << "MONTH" << setw(14) << "BALANCE DUE" << endl;}float calculateBalanceDue(float balanceDue, float paymentAmt){    //Calculate and return the balance due    balanceDue -= paymentAmt;        return balanceDue;}void displayBalance(int month, float balanceDue){    if (month == 10) {        //Display the month and the balance due        cout << month << setw(10) << balanceDue << endl;    }        else    {        //Display the month and the balance due        cout << month << setw(14) << balanceDue << endl;    }}